INSERT INTO `boeno_article` (`article_id`, `lanmu_id`, `title`, `image`, `description`, `keywords`, `liulan`, `uptime`, `class_id`, `zhuangtai`, `content`) VALUES
(1, 8, '（风格系列）尤记那些旧时光------（中华新天地）', '549e2554d0718.jpg', '		这个世界上最不可信就是开发商的广告了卖楼的宣传单上总是充满了诱惑什么破位置烂楼盘到了他们的广告语里都能点石成金	位置好的大肆宣传黄金地段全城绝版偏到北大荒的也能吹成了幽静园林潜力无限	忽悠忽悠再忽悠人们在房奴的路上前仆后继被敲骨剥髓却又乐此不疲	但总有一些人会选择与众', '', '100', '2014-12-28', 33, '1', '<p style="color:#DAC9A4;font-family:Simsun;background-color:#000000;">\r\n	<img title="banner.jpg" border="0" src="http://www.jzjhzs.com/admin/ueditor/server/upload/uploadimages/7391401091105.jpg" width="605" height="256" style="width:556px;height:232px;" /> \r\n</p>\r\n<p style="color:#DAC9A4;font-family:Simsun;background-color:#000000;">\r\n	这个世界上最不可信<br />\r\n就是开发商的广告了<br />\r\n卖楼的宣传单上总是充满了诱惑<br />\r\n什么破位置烂楼盘<br />\r\n到了他们的广告语里都能点石成金\r\n</p>\r\n<p style="color:#DAC9A4;font-family:Simsun;background-color:#000000;">\r\n	位置好的大肆宣传黄金地段全城绝版<br />\r\n偏到北大荒的也能吹成了幽静园林潜力无限\r\n</p>\r\n<p style="color:#DAC9A4;font-family:Simsun;background-color:#000000;">\r\n	忽悠忽悠再忽悠<br />\r\n人们在房奴的路上前仆后继&nbsp;<br />\r\n被敲骨剥髓却又乐此不疲\r\n</p>\r\n<p style="color:#DAC9A4;font-family:Simsun;background-color:#000000;">\r\n	但总有一些人<br />\r\n会选择与众不同的路<br />\r\n于是&nbsp;他们的人生&nbsp;从不拥挤<br />\r\n比如&nbsp;这套盖在山边的别墅<br />\r\n便是一个很好的案例\r\n</p>\r\n<p style="color:#DAC9A4;font-family:Simsun;background-color:#000000;">\r\n	建筑成本八百块一平米<br />\r\n如果能在山边有块地&nbsp;<br />\r\n为什么不按自己的意愿<br />\r\n盖一所自己喜欢的房子？\r\n</p>\r\n<p style="color:#DAC9A4;font-family:Simsun;background-color:#000000;">\r\n	影视城的山脚下<br />\r\n空气清新&nbsp;却又交通便利<br />\r\n远吗？你错了&nbsp;<br />\r\n在城市里&nbsp;远不是问题<br />\r\n堵才是距离\r\n</p>\r\n<p style="color:#DAC9A4;font-family:Simsun;background-color:#000000;">\r\n	当你还在高峰里寸步难行<br />\r\n人家已经坐在自家的院子里看傍晚的霞光了\r\n</p>\r\n<p style="color:#DAC9A4;font-family:Simsun;background-color:#000000;">\r\n	亲，追不上房价脚步&nbsp;咱回老家盖房子吧<br />\r\n种菜养鸡栽果树&nbsp;养花养鱼逗小狗<br />\r\n晒晒太阳&nbsp;&nbsp;呼吸新鲜的空气<br />\r\n当个农村人吧&nbsp;轻松生活&nbsp;<br />\r\n就这样老去\r\n</p>\r\n<p style="color:#DAC9A4;font-family:Simsun;background-color:#000000;">\r\n	<img title="1 副本.jpg" src="http://www.jzjhzs.com/admin/ueditor/server/upload/uplo